var max_duration = .1;
var colorArray = ['#FF6633','#4D4D4D','#5DA5DA','#FAA43A','#60BD68','#F17CB0','#B2912F','#B276B2','#DECF3F','#F15854'];

// If cache was passed in, then assume it came from the perist node.
// Set it in context to restore the chart state
if(typeof(msg.cache) === "object"){
    context.set('cache',msg.cache);
}

var cache = context.get('cache');
if(typeof(cache) === "undefined"){
    // node.warn("Init cache");
    cache = {
        labels: [],
        datasets: [],

    }
}

if(typeof(msg.topic) === "string" && typeof(msg.payload) === "boolean"){
    // Begin history tracking
    // This topic is new so init the object and push it onto the labels and datasets arrays
    if(cache.labels.indexOf(msg.topic) == -1){
        // node.warn("create new label");
        cache.labels.push(msg.topic);

        cache.datasets.push({
            label: msg.topic,
            current: false,
            timestamp: new Date(),
            data: []
        });
    }

    var thisIndex = cache.labels.indexOf(msg.topic);

    // The state has changed
    if(msg.payload != cache.datasets[thisIndex].current){
        cache.datasets[thisIndex].current = msg.payload;
        cache.datasets[thisIndex].timestamp = new Date();

        // Only add a new event if the state switched from false to true
        if(msg.payload === true){
            // add a new start/stop event to the topics history
            var event = [new Date(), new Date(), colorArray[thisIndex]];
            cache.datasets[thisIndex].data.push(event);
        }
    }
}


cache.datasets.forEach(function(topic){
    // The event is still active so
    if(topic.current === true && topic.data.length){
        // Update the event stop time to the current time
        topic.data[topic.data.length - 1][1] = new Date();
    }


    topic.data.forEach(function(event, index, object){
        // cleanup old history (anything with an ending time more than max_duration hours old)
        if(new Date() - event[1] > max_duration * 3600000){
            // Delete the event
            object.splice(index,1);
        }
    });
});


context.set('cache',cache);

msg.cache = cache;

return msg;

