// split the topic into two parts
var parts = msg.topic.split('/');
var topic = '';
var base = '';
var n    = 0;
for (var i of parts) {
    if (n > 1) {
        topic = topic + i ;
        if (i !== parts.length[i] ) {
            topic = topic + '/';
        }
    }
    n++;
}
base = parts[0] + '/' + parts[1] + '/' ;

flow.set('Devicepath', base);
msg.topic = topic.substring(0,topic.length - 1);
msg.base  = base;
//msg.parts  = parts;
return msg;

// Authored by Michael Thompson on 30/12/2020
