// Updates flow array containing all topics
// May or may not be necessary // 
const topicArray = flow.get("topicArray") || [];
if ((topicArray.includes(msg.topic))===false)
    topicArray.push(msg.topic);
flow.set("topicArray", topicArray);


var group = flow.get('Devicepath');

// Updates flow containing for groups
const groups = flow.get("groups") || [];
if (!(groups.includes(group)))
    groups.push(group);
flow.set("groups", groups);

// Updates flow containing for topics within current group
const groupArray = flow.get(group+"Array") || [];
if (!(groupArray.includes(msg.topic)) && !msg.topic.includes("$"))
    groupArray.push(msg.topic);
flow.set(group+"Array",groupArray);

// Updates flow variable for current group and topic
flow.set(group+msg.topic,
        // WILL NEED TO PARSE OTHER DATATYPES
        // PARSING FLOAT BECAUSE FIRMWARE SENDS ALL DATA AS STRINGS
        // WHICH CAUSES ERRORS WITH INFLUXDB TYPES
        {"value":parseFloat(msg.payload),
        // Bool for if data is new
         "new":true
        });
